// Instruction format:
// [template_id: u8][proof_a: 64 bytes][proof_b: 128 bytes][proof_c: 64 bytes][public_inputs: n * 32 bytes]
pub fn process_instruction(
    _program_id: &Pubkey,
    _accounts: &[AccountInfo],
    instruction_data: &[u8],
) -> ProgramResult {
    msg!("zkRune On-Chain Groth16 Verification");
    
    // Minimum size: 1 (template_id) + 64 (proof_a) + 128 (proof_b) + 64 (proof_c) + 96 (3 * 32 bytes for age-verification)
    if instruction_data.len() < 353 {
        msg!("Error: Instruction data too short: {} bytes", instruction_data.len());
        return Err(ProgramError::InvalidInstructionData);
    }
    
    let template_id = instruction_data[0];
    msg!("Template ID: {}", template_id);
    
    // Currently only supporting age-verification (template_id = 0)
    if template_id != 0 {
        msg!("Error: Only age-verification (ID=0) is supported currently");
        return Err(ProgramError::InvalidArgument);
    }
    
    // Parse proof components
    let mut offset = 1;
    
    // Proof A (64 bytes - G1 point)
    let proof_a: &[u8; 64] = instruction_data[offset..offset + 64]
        .try_into()
        .map_err(|_| ProgramError::InvalidInstructionData)?;
    offset += 64;
    
    // Proof B (128 bytes - G2 point)
    let proof_b: &[u8; 128] = instruction_data[offset..offset + 128]
        .try_into()
        .map_err(|_| ProgramError::InvalidInstructionData)?;
    offset += 128;
    
    // Proof C (64 bytes - G1 point)
    let proof_c: &[u8; 64] = instruction_data[offset..offset + 64]
        .try_into()
        .map_err(|_| ProgramError::InvalidInstructionData)?;
    offset += 64;
    
    // Public inputs (3 * 32 bytes for age-verification)
    if instruction_data.len() < offset + 96 {
        msg!("Error: Not enough data for public inputs");
        return Err(ProgramError::InvalidInstructionData);
    }
    
    // Manual parsing since we can't directly convert &[u8] to &[[u8; 32]; 3]
    let input_1: [u8; 32] = instruction_data[offset..offset + 32]
        .try_into()
        .map_err(|_| ProgramError::InvalidInstructionData)?;
    let input_2: [u8; 32] = instruction_data[offset + 32..offset + 64]
        .try_into()
        .map_err(|_| ProgramError::InvalidInstructionData)?;
    let input_3: [u8; 32] = instruction_data[offset + 64..offset + 96]
        .try_into()
        .map_err(|_| ProgramError::InvalidInstructionData)?;
    
    let public_inputs: [[u8; 32]; 3] = [input_1, input_2, input_3];
    
    msg!("Creating Groth16 verifier...");
    
    // Create verifier
    let mut verifier = Groth16Verifier::new(
        proof_a,
        proof_b,
        proof_c,
        &public_inputs,
        &AGE_VERIFICATION_VK,
    ).map_err(|e| {
        msg!("Error creating verifier: {:?}", e);
        ProgramError::InvalidArgument
    })?;
    
    msg!("Verifying proof on-chain...");
    
    // Perform verification (uses altbn254 syscalls)
    verifier.verify().map_err(|e| {
        msg!("Verification FAILED: {:?}", e);
        ProgramError::InvalidArgument
    })?;
    
    msg!("âœ… PROOF VERIFIED ON-CHAIN!");
    msg!("ðŸ” Cryptographic verification successful");
    msg!("âš¡ Using altbn254 syscalls");
    
    Ok(())
}
